var button=document.querySelector(".choice-block"),close=document.querySelector(".btn"),popup=document.querySelector(".hotel-search"),checkin=popup.querySelector("[name=check-in]");button.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("modal-content-show"),checkin.focus()}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("modal-content-show")}),window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("modal-content-show")&&popup.classList.remove("modal-content-show")});